mapper.select:
select calories_burnt as caloriesBurnt, date, date_created as dateCreated, date_updated as dateUpdated, duration, id, intensity, title, uid, workout_type as workoutType from workout_logs where uid = #{userId} and date <= current_date
and workout_type = #{workoutType}
and intensity = #{intensity}
order by date desc limit #{pageSize} offset #{offset}
